def split_list(list):
    result = []
    for count, value in enumerate(list):
        splitted = value.split('\\n')
        splitted = value.split('\n')        
        separator = '- : -'
        how_many_games = splitted.count(separator)
        # print(f'\n\nhow_many_games: {how_many_games}')
        start_search = 0 
        # print(f'len(splitted_string): {len(splitted)}')        
        for x in range(how_many_games):
            current_position = splitted.index(separator, start_search)
            # print(f'start_search: {start_search}')
            # print(f'current_position: {current_position}')
            # if x == 0:
            #     print(f'splitted: {splitted}')
            # print(f'\nsplitted[0]: {splitted[0]}') # date
            
            # print(f'splitted[4]: {splitted[4]}') # time
            # print(f'splitted[current_position - 3]: {splitted[current_position - 3]}') # time            

            # print(f'splitted[6]: {splitted[6]}') # Team A
            # print(f'splitted[current_position - 1]: {splitted[current_position - 1]}') # Team A            

            # print(f'splitted[8]: {splitted[8]}') # Team B
            # print(f'splitted[current_position + 1]: {splitted[current_position + 1]}') # Team B            

            # print(f'splitted[9]: {splitted[9]}') # W1
            # print(f'splitted[current_position + 2]: {splitted[current_position + 2]}') # W1            

            # print(f'splitted[10]: {splitted[10]}') # Draw
            # print(f'splitted[current_position + 3]: {splitted[current_position + 3]}') # Draw            

            # print(f'splitted[11]: {splitted[11]}') # W2
            # print(f'splitted[current_position + 4]: {splitted[current_position + 4]}') # W2            

            result.append(['FonBet',\
                            'World cup 2022',\
                            splitted[0],\
                            splitted[current_position - 3],\
                            splitted[current_position - 1],\
                            splitted[current_position + 1],\
                            splitted[current_position + 2],\
                            splitted[current_position + 3],\
                            splitted[current_position + 4]
                            ])
            start_search = current_position+1
        # print(f'\n\ncount:\n {count}\n value:\n{value}')
    # print(f'\n {result}')
    return result
    
