def split_list(list):
    result = []
    for count, value in enumerate(list):
        splitted = value.split('\\n')
        splitted = value.split('\n')
        # print(f'splitted:\n{splitted}')
        separator = ':'        
        how_many_games = [x for x in splitted if separator in x]
        # print(f'\n\nhow_many_games: {how_many_games}')
        # print(f'\n\nlen(how_many_games): {len(how_many_games)}')
        start_search = 0 
        # print(f'len(splitted): {len(splitted)}')           
        for x in range(len(how_many_games)):
            current_position = splitted.index(how_many_games[x], start_search)
            # print(f'\nx: {x}')
            # print(f'how_many_games[x]: {how_many_games[x]}')
            # print(f'start_search: {start_search}')
            # print(f'current_position: {current_position}')
            index_of_date = splitted[current_position].index('в')
            # print(f'index_of: {index_of_date}')
            date_of_game = splitted[current_position][:index_of_date-1]
            time_of_game = splitted[current_position][index_of_date+1:]
            # print(f'date_of_game: {date_of_game}')
            # print(f'time_of_game: {time_of_game}')
            # print(f'splitted[current_position - 1]: {splitted[current_position - 1]}')
            index_of_team = splitted[current_position - 1].index('—')
            team_A = splitted[current_position - 1][:index_of_team-1]
            team_B = splitted[current_position - 1][index_of_team+1:]            
            result.append(['BetCity',\
                            'World cup 2022',\
                            date_of_game,\
                            time_of_game,\
                            team_A,\
                            team_B,\
                            splitted[current_position + 1],\
                            splitted[current_position + 2],\
                            splitted[current_position + 3]
                            ])
            
            start_search = current_position + 1        
    print(f'\nresult:\n {result}')
    return result
    
